import React, {useEffect, useState} from 'react';
import {Badge, Box, Card, CardActionArea, CardContent, CardMedia, Container, Typography} from "@mui/material";
import axios from "axios";
import CardComponent from "../../../../components/CardComponent";

type Course = {
    id: number
    name: string
    description: string
    price: number,
    topicId: number
}

function AllCoursesPage() {

    const [courses, setCourses] = useState<Course[]>([]);

    const fetchCourses = async () => {
        try {
            const response = await axios.get(`${process.env.REACT_APP_SERVER_URL}/api/course`)
            console.log(response.data);
            setCourses(response.data)
        } catch (e) {
            console.log(e)
        }
    }

    useEffect(() => {
        fetchCourses();
    }, []);

    return (
        <Container sx={{ width: 1 }}>
            <h1 className={"text-left text-xl p-6"}>All Courses</h1>
            <Box display="flex" justifyContent="center" flexWrap={"wrap"} gap={3}>
                {courses.map((course, index) => (
                    <CardComponent key={index} description={course.description} price={course.price} id={course.id} topicId={course.topicId} name={course.name} index={index+1}/>
                ))}
            </Box>
        </Container>
    );
}

export default AllCoursesPage;
