import React from 'react';
import {Card, Box, CardActionArea, CardContent, Typography, Badge} from "@mui/material";
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import {CZK} from "../utils/utils";

type Course = {
    id: number
    name: string
    description: string
    price: number,
    topicId: number
}

function CardComponent({description, price, id, topicId, name}: Course) {
    return (
        <Card sx={{ width: 345, height:300 }}>
            <Box sx={{height:1/10}}>
                <Badge color="secondary" badgeContent={CZK.format(price)}>
                    <LocalOfferIcon />
                </Badge>
            </Box>
            <CardActionArea sx={{height:4/6}}>
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {description}
                    </Typography>
                </CardContent>
            </CardActionArea>

            <Box sx={{ bgcolor: 'error.main', color: 'error.contrastText', p: 2, height:1/6 }}>
                {topicId}
            </Box>
        </Card>
    );
}

export default CardComponent;
