import React, {useEffect, useState} from 'react';
import axios from "axios";
import TextField from '@mui/material/TextField';
import {SubmitHandler, useForm} from "react-hook-form";
import {Button, Container, Grid, MenuItem, Select} from "@mui/material";
import {makeStyles} from "@material-ui/core/styles";
import MessageBox from "../../../../components/Messages/MessageBox";

type Course = {
    name: string
    description: string
    price: number
}

const useStyles: any = makeStyles((theme: any) => ({
    root: {
        "& .MuiTextField-root": {
            width: "100%",
        },
        "& .MuiOutlinedInput-root": {
            borderRadius: "12px",
        },
    },
}));

function CreateCoursePage() {


    const [statusCode, setStatusCode] = useState<number>(0)
    const [messages, setMessages] = useState<string[]>([]);

    const {
        register,
        handleSubmit,
        formState: {errors},
    } = useForm<Course>()
    const onSubmit: SubmitHandler<Course> = async (data) => {
        try {
            const response = await axios.post("/api/course", {
                name: data.name,
                description: data.description,
                price: data.price
            })
            console.log(response.data)
            setStatusCode(response.status)
            setMessages([response.data])
        } catch (e) {
            setMessages(["Something went wrong"])
            setStatusCode(400)
        }
    }

    const classes = useStyles();


    useEffect(() => {
        Object.entries(errors).map(([key, value]) => {
            setMessages(prev => [...prev, String(value.message)])
        })
        setStatusCode(400)
    }, [errors])


    const [topics, setTopics] = useState<string[]>([])
    const getAllTopics = async () => {
        try {
            const response = await axios.get("/api/topic")
            setTopics(response.data)
        } catch (e) {
            setMessages(["Cannot fetch topics"])
        }
    }

    useEffect(() => {
        getAllTopics();
    }, [])

    return (
        <Container>
            <h1 className={"text-left text-xl p-6"}>Create Course</h1>
            <form className={classes.root} onSubmit={handleSubmit(onSubmit)}>
                <Grid spacing={3} p={4} container>
                    <Grid item xs={12} sm={12} md={6}>
                        <TextField id="outlined-basic" label="Course Name" variant="outlined"
                                   defaultValue="test" type={"text"} {...register("name", {
                            required: "Name of Course is required", maxLength: {
                                value: 100,
                                message: "The Course Name cannot be so long"
                            }
                        })}/>
                    </Grid>
                    <Grid item xs={12} sm={12} md={6}>
                        <TextField id="outlined-basic" label="Course Description"
                                   variant="outlined" type={"text"} {...register("description", {
                            required: "Description of Course is required",
                            maxLength: 1000
                        })}
                        />
                    </Grid>
                    <Grid item xs={12} sm={12} md={6}>
                        <TextField id="outlined-basic" label="Course Price"
                                   variant="outlined"
                                   type={"number"} {...register("price", {required: "Price for Course is required"})}/>

                    </Grid>
                    <Grid item xs={12} sm={12} md={6}>
                        <Select
                            labelId="demo-multiple-name-label"
                            id="demo-multiple-name"
                            multiple
                            // value={personName}
                            // onChange={handleChange}
                            // input={<OutlinedInput label="Name" />}
                            // MenuProps={MenuProps}
                        >
                            {topics.map((topic, index) => (
                                <MenuItem
                                    key={index}
                                    value={topic}
                                >
                                    {topic}
                                </MenuItem>
                            ))}
                        </Select>

                    </Grid>
                    <Grid item xs={12}>
                        <Button variant="contained" color="success" type={"submit"}>
                            Create Course
                        </Button>
                    </Grid>
                </Grid>

                {
                    messages.length > 0 && <MessageBox messages={messages} status={statusCode}/>
                }
            </form>
        </Container>
    );
}

export default CreateCoursePage;
