import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {SubmitHandler, useForm} from "react-hook-form";
import {Button, Grid} from "@mui/material";
import {makeStyles} from "@material-ui/core/styles";
import InputAdornment from "@material-ui/core/InputAdornment";
import AccountCircle from "@material-ui/icons/AccountCircle";

type Course = {
    name: string
    description: string
    price: number
}

const useStyles: any = makeStyles((theme: any) => ({
    root: {
        "& .MuiTextField-root": {
            width: "100%",
        },
        "& .MuiOutlinedInput-root": {
            borderRadius: "12px",
        },
    },
}));

function CreateCoursePage() {
    const {
        register,
        handleSubmit,
        formState: {errors},
    } = useForm<Course>()
    const onSubmit: SubmitHandler<Course> = (data) => console.log(data)

    const classes = useStyles();

    console.log(errors)
    Object.keys(errors).map((error, index) => {
        console.log(error)
    })


    return (
        <>
            <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit(onSubmit)}>
                <Grid container spacing={3} p={4}>
                    <Grid item xs={12} sm={5} md={4}>
                        <TextField
                            id="outlined-basic"
                            variant="outlined"
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <AccountCircle/>
                                    </InputAdornment>
                                ),
                                endAdornment: (
                                    <InputAdornment position="end">
                                        <p>$</p>
                                    </InputAdornment>
                                ),
                            }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={5} md={4}>
                        <TextField id="outlined-basic" label="Course Name" variant="outlined"
                                   defaultValue="test" type={"text"} {...register("name", {
                            required: "Name of Course is required", maxLength: {
                                value: 3,
                                message: "The Course Name cannot be so long"
                            }
                        })}/>
                    </Grid>
                    <Grid item xs={12} sm={5} md={4}>
                        <TextField id="outlined-basic" label="Course Description"
                                   variant="outlined" type={"text"} {...register("description", {
                            required: "Description of Course is required",
                            maxLength: 1000
                        })}
                        />
                    </Grid>
                    <Grid item xs={12} sm={5} md={4}>
                        <TextField id="outlined-basic" label="Course Price"
                                   variant="outlined"
                                   type={"number"} {...register("price", {required: "Price for Course is required"})}/>

                    </Grid>
                    <Grid item xs={12}>
                        <Button variant="contained" color="success" type={"submit"}>
                            Create Course
                        </Button>
                    </Grid>
                </Grid>

                <div>
                    {Object.entries(errors).map(([key, value]) => (
                        <p key={key}>{`${key}: ${value.message}`}</p>
                    ))}
                </div>
            </form>
        </>
    );
}

export default CreateCoursePage;
