import React from 'react';
import {Avatar, Box, Card, CardContent, CardHeader, IconButton, Typography} from "@mui/material";
import {red} from "@mui/material/colors";
import DeleteIcon from '@mui/icons-material/Delete';
import TagIcon from '@mui/icons-material/Tag';

type CardUserProps = {
    id: number
    firstname: string
    lastname: string
    email: string
    phone: number
    courses: Course[]
}

type Course = {
    id: number
    name: string
    description: string
    price: number
    topic: Topic
}
type Topic = {
    id: number
    name: string
}

function CardUser({id, firstname, lastname, email,  phone, courses}:CardUserProps) {
    return (
        <Card sx={{width: 400}}>
            <CardHeader
                avatar={
                    <Avatar sx={{bgcolor: red[500]}} aria-label="recipe">
                        {firstname[0]}{lastname[0]}
                    </Avatar>
                }
                action={
                    <IconButton>
                        <DeleteIcon sx={{color:"red"}}/>
                    </IconButton>
                }
                title={`${firstname} ${lastname}`}
                subheader={`${phone}, ${email}`}
            />
            <CardContent sx={{display:"flex", flexWrap:"wrap", gap:3}}>
                {
                    courses.map((course, index) => (
                        <Box key={index} sx={{
                            display:"flex",
                            flexWrap:"wrap",
                            flexDirection:"column",
                            padding:1,
                            backgroundColor:"#ffebee",
                            borderRadius:3
                        }}>
                            <Typography sx={{textAlign: "left", padding:1}}>{course.name}</Typography>
                            <Box  sx={{
                                borderRadius: 1,
                                bgcolor: 'error.main',
                                color:"white"
                            }}>
                                <Box>
                                    <TagIcon></TagIcon>
                                    <Typography sx={{fontSize:14}}>   {course.topic.name}</Typography>
                                </Box>
                            </Box>
                        </Box>
                    ))
                }
            </CardContent>
        </Card>
    );
}

export default CardUser;
