import React from 'react';
import {Avatar, Box, Card, CardContent, CardHeader, IconButton, Typography} from "@mui/material";
import {red} from "@mui/material/colors";

type CardUserProps = {
    id: number
    firstname: string
    lastname: string
    email: string
    phone: number
    courses: Course[]
}

type Course = {
    id: number
    name: string
    description: string
    price: number
    topic: Topic
}
type Topic = {
    id: number
    name: string
}

function CardUser({id, firstname, lastname, email,  phone, courses}:CardUserProps) {
    return (
        <Card sx={{width: 400}}>
            <CardHeader
                avatar={
                    <Avatar sx={{bgcolor: red[500]}} aria-label="recipe">
                        {firstname[0]}{lastname[0]}
                    </Avatar>
                }
                action={
                    <IconButton aria-label="settings">
                        xd
                    </IconButton>
                }
                title={`${firstname} ${lastname}`}
                subheader={`${phone}, ${email}`}
            />
            <CardContent>
                {
                    courses.map((course, index) => (
                        <Box key={index} sx={{
                            borderRadius: 1,
                            bgcolor: 'secondary.light',
                            bgcolor: 'secondary.light',
                            color:"white",
                            margin:1
                        }}>
                            <Typography variant={"h6"} sx={{textAlign: "left"}}>{course.name}</Typography>
                            <Box  sx={{
                                borderRadius: 1,
                                bgcolor: 'error.main',
                                color:"white"
                            }}>
                                {course.topic.name}
                            </Box>
                        </Box>
                    ))
                }
            </CardContent>
        </Card>
    );
}

export default CardUser;
