import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {SubmitHandler, useForm} from "react-hook-form";
import {Button} from "@mui/material";

type Course = {
    name: string
    description: string
    price: number
}


function CreateCoursePage() {

    const {
        register,
        handleSubmit,
        watch,
        formState: {errors},
    } = useForm<Course>()
    const onSubmit: SubmitHandler<Course> = (data) => console.log(data)

    return (
        <Box sx={{
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            alignItems: "center",
            width: "100%"
        }}>
            <Box
                component="form"
                sx={{
                    '& > :not(style)': {m: 1},
                    p: 10,
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "flex-start",
                    flexWrap: "wrap"
                }}
                noValidate
                autoComplete="off"
                onSubmit={handleSubmit(onSubmit)}
            >
                <TextField id="outlined-basic" label="Course Name" variant="outlined"
                           defaultValue="test" {...register("name")} sx={{width: "50ch"}}/>
                <TextField id="outlined-basic" label="Course Description"
                           variant="outlined" {...register("description", {required: true})}  sx={{width: "50ch"}}/>
                <TextField id="outlined-basic" label="Course Price"
                           variant="outlined" {...register("price", {required: true})}  sx={{width: "25ch"}}/>
                <Box sx={{width: 1}}>
                    <Button variant="contained" color="success" type={"submit"}>
                        Create Course
                    </Button>
                </Box>
            </Box>

            {/* errors will return when field validation fails  */}
            {errors.description && <span>This field is required</span>}
        </Box>
    );
}

export default CreateCoursePage;
