import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {Form, SubmitHandler, useForm} from "react-hook-form";
import {FormControl} from "@mui/material";

type Course = {
    name: string
    description: string
    price: number
}


function CreateCoursePage() {

    const {
        register,
        handleSubmit,
        watch,
        formState: {errors},
    } = useForm<Course>()
    const onSubmit: SubmitHandler<Course> = (data) => console.log(data)

    console.log(watch("name")) // watch input value by passing the name of it
    return (
        <>
        <FormControl>
            <Box
                component="form"
                sx={{
                    '& > :not(style)': {m: 1, width: '25ch'},
                }}
                noValidate
                autoComplete="off"
            >
                <TextField id="outlined-basic" label="Course Name" variant="outlined" defaultValue="test" {...register("name")} />
                <TextField id="outlined-basic" label="Course Description" variant="outlined" {...register("description", { required: true })}/>
                <TextField id="outlined-basic" label="Course Price" variant="outlined" {...register("price", { required: true })}/>
            </Box>
        </FormControl>


            <form onSubmit={handleSubmit(onSubmit)}>


                <input type={"submit"} />
            </form>
            {/* errors will return when field validation fails  */}
            {errors.description && <span>This field is required</span>}
        </>
    );
}

export default CreateCoursePage;
