import React from 'react';
import {Card, Box, CardActionArea, CardContent, CardMedia, Typography} from "@mui/material";

type Course = {
    id: number
    name: string
    description: string
    price: number,
    topicId: number
}

function CardComponent({description, price, id, topicId, name}: Course) {
    return (
        <Card sx={{ width: 345, height:300 }}>
            <CardActionArea sx={{height:5/6}}>
                <CardMedia
                    component="img"
                    height="140"
                    image="/static/images/cards/contemplative-reptile.jpg"
                    alt="green iguana"
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {description}
                    </Typography>
                </CardContent>
            </CardActionArea>

            <Box sx={{ bgcolor: 'error.main', color: 'error.contrastText', p: 2 }}>
                {topicId}
            </Box>
        </Card>
    );
}

export default CardComponent;
