import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {SubmitHandler, useForm} from "react-hook-form";

type Inputs = {
    example: string
    exampleRequired: string
}


function CreateCoursePage() {

    const {
        register,
        handleSubmit,
        watch,
        formState: {errors},
    } = useForm<Inputs>()
    const onSubmit: SubmitHandler<Inputs> = (data) => console.log(data)

    console.log(watch("example")) // watch input value by passing the name of it
    return (
        <>
            <form onSubmit={handleSubmit(onSubmit)}>
                <Box
                    component="form"
                    sx={{
                        '& > :not(style)': {m: 1, width: '25ch'},
                    }}
                    noValidate
                    autoComplete="off"
                >
                    <TextField id="outlined-basic" label="Course Name" variant="outlined" defaultValue="test" {...register("name")} />
                    <TextField id="outlined-basic" label="Course Description" variant="outlined" {...register("exampleRequired", { required: true })}/>
                    <TextField id="outlined-basic" label="Course Price" variant="outlined" {...register("exampleRequired", { required: true })}/>
                </Box>

                {/* errors will return when field validation fails  */}
                {errors.exampleRequired && <span>This field is required</span>}
            </form>
        </>
    );
}

export default CreateCoursePage;
