import React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {SubmitHandler, useForm} from "react-hook-form";
import {Button, Grid} from "@mui/material";
import { makeStyles } from "@material-ui/core/styles";
import Input from "@material-ui/core/Input";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import AccountCircle from "@material-ui/icons/AccountCircle";

type Course = {
    name: string
    description: string
    price: number
}

const useStyles: any = makeStyles((theme: any) => ({
    root: {
        "& .MuiTextField-root": {
            width: "100%",
        },
        "& .MuiOutlinedInput-root": {
            borderRadius: "12px",
        },
    },
}));

function CreateCoursePage() {

    const {
        register,
        handleSubmit,
        formState: {errors},
    } = useForm<Course>()
    const onSubmit: SubmitHandler<Course> = (data) => console.log(data)

    const classes = useStyles();


    return (
        <>
            <Box
                component="form"
                sx={{
                    '& > :not(style)': {m: 1},
                    p: 10,
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "flex-start",
                    flexWrap: "wrap"
                }}
                noValidate
                autoComplete="off"
                onSubmit={handleSubmit(onSubmit)}
            >

                {/* errors will return when field validation fails  */}
                {errors.description && <span>This field is required</span>}
            </Box>

            <form className={classes.root} noValidate autoComplete="off">
                <Grid container spacing={10}>
                    <Grid item xs={12} sm={5} md={4}>
                        <TextField
                            id="outlined-basic"
                            variant="outlined"
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <AccountCircle/>
                                    </InputAdornment>
                                ),
                                endAdornment: (
                                    <InputAdornment position="end">
                                        <p>$</p>
                                    </InputAdornment>
                                ),
                            }}
                        />
                    </Grid>

                    <Grid item xs={12} sm={5} md={4}>
                        <TextField id="outlined-basic" label="Course Name" variant="outlined"
                                   defaultValue="test" {...register("name")} sx={{width: "50ch"}}/>
                    </Grid>
                    <Grid item xs={12} sm={5} md={4}>
                        <TextField id="outlined-basic" label="Course Description"
                                   variant="outlined" {...register("description", {required: true})}
                                   sx={{width: "50ch"}}/>
                    </Grid>
                    <Grid item xs={12} sm={5} md={4}>
                        <TextField id="outlined-basic" label="Course Price"
                                   variant="outlined" {...register("price", {required: true})} sx={{width: "25ch"}}/>

                    </Grid>
                    <Grid item xs={12}>
                        <Button variant="contained" color="success" type={"submit"}>
                            Create Course
                        </Button>
                    </Grid>
                </Grid>

            </form>
        </>
    );
}

export default CreateCoursePage;
